void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5*iResolution.xy) / iResolution.y;
    fragColor = vec4(1.0);
    float w = 0.1;
    if(( abs(uv.x) < 3.0 * w && abs(uv.y) < 3.0 * w )){ 
        fragColor = vec4(1.0, 0.0, 0.0,0.0);
    }
    if( abs(uv.x) > w  && abs(uv.y) > w ){
        fragColor = vec4(1.0);
    }

}
