float Circle(vec2 uv, vec2 pos, float r, float blur) {
    return smoothstep(r,r-blur-0.0001,length(uv-pos));
}

float Rectangle(vec2 uv, vec2 pos) {return 1.;}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x *= iResolution.x/iResolution.y;

    float x = uv.x;
    float y = uv.y;

    float d = length(uv);
    uv*=sin(uv.x)*13.;
    uv*=uv.y;
    uv.x = fract(uv.x);
    uv.y = fract(uv.y);

    // Time varying pixel color
    vec3 col = vec3(0);

    col += vec3(uv.x,uv.y,uv.x*uv.y)*smoothstep(8., 0., Circle(uv,vec2(sin(iTime*5.*uv.x),cos(iTime*5.*uv.y)),1.,0.05)/(abs(x)+abs(y)));
    col -= abs(x*y);


    // Output to screen
    fragColor = vec4(col,1.0);
}
