//Thanks to Inigo Quilez for the PRNG and normal function


int seed = 3;
float arpeture_size = 5.0;
vec3 rgb;
int frand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
float rand(void) { return float(frand())/32767.0; }
vec3 point;
vec3 lightpos = vec3(46,100.0,60.0);
float hash1( uint n ) {
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uint(0x7fffffffU))/float(0x7fffffff);
}


float sde;
vec3 pos;
float maxl = 10000.0;


float plane_sde(vec3 ray, vec3 nor, float planey, vec3 col){
    if (dot(ray,nor)+planey < sde){
        sde = dot(ray,nor)+planey;
        rgb = col;
    }
    return dot(ray,nor)+planey;
}


float sphere_sde(vec3 ray, vec3 position, float radius, vec3 col){
    if (length(position-ray)-radius < sde){
        sde = length(position-ray)-radius;
        rgb = col;
    }
    return length(position-ray)-radius;
}


float calc_sdf(vec3 ray){
    sde = maxl;
    plane_sde(ray,vec3(0,1,0),10.0,vec3(0,0,1));
    plane_sde(ray,vec3(1,0,0),30.0,vec3(0,1,0));
    sphere_sde(ray,vec3 (-10.0,11.0,80.0),20.0, vec3(1,0,0));
    sphere_sde(ray,vec3 (20.0,2.0,100.0),10.0, vec3(0,1,1));
    sphere_sde(ray,vec3 (-20.0,-5.0,50.0),5.0, vec3(1,0.5,0.1));
    return sde;
}


vec3 calcNormal( in vec3 p ) // for function f(p)
{
    const float eps = 0.0001; // or some other value
    const vec2 h = vec2(eps,0);
    return normalize( vec3(calc_sdf(p+h.xyy) - calc_sdf(p-h.xyy),
                           calc_sdf(p+h.yxy) - calc_sdf(p-h.yxy),
                           calc_sdf(p+h.yyx) - calc_sdf(p-h.yyx) ) );
}


vec4 raycol(vec3 orig, vec3 dir, float ml){
    float len = 0.0;
    sde = ml;
    float steps = 0.0;
    pos = orig;

    while (len < ml && sde > 0.01){
        sde = calc_sdf(pos);
        pos += dir*sde;
        len += sde;
        steps += 1.0;
    }
    return vec4(pos,steps);
}


vec3 sphrand(){
    point = vec3(2.0);
    while(length(point)>=1.0){
        point = vec3(rand()*2.0-1.0,rand()*2.0-1.0,rand()*2.0-1.0);
    }
    return point;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 newfrag = vec2(fragCoord.x+iResolution.x/2.0,fragCoord.y+iResolution.y/2.0); 
    uvec2 p = uvec2((newfrag-.5*iResolution.xy)/iResolution.y*1000.0);
    seed = int(round(hash1( p.x + 1920U*p.y + (1920U*1080U))*100.0*float(iFrame)));
    vec2 th = fragCoord/iResolution.xy;
    vec3 colour = vec3(0);
    for (int i = 0; i<1; i++){
        vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y+sphrand().xy/iResolution.xy;
        vec3 pos = vec3(sphrand().x*arpeture_size,sphrand().y*arpeture_size,-1.0);
        vec3 direction = normalize(normalize(vec3(uv.x,uv.y,1.0))*80.0-pos);
        vec4 o;
        float steps;
        o = raycol(pos,direction,500.0);
        if (sde <= 0.1){
            steps =o.w;
            vec3 inter = o.xyz;
            vec3 pre = rgb;
            //colour = rgb;
            vec3 newdir = sphrand();
            if (dot(newdir,calcNormal(inter))<0.0){
                newdir *= -1.0;
            }
            vec3 po = raycol(inter+calcNormal(inter)/10.0,newdir,500.0).xyz;
            po += calcNormal(po)/10.0;
            vec3 offset = sphrand();
            offset *= 50.0;
            vec3 te = rgb;
            vec3 hit = raycol(po,normalize((lightpos+offset)-po),400.0).xyz;
            if (length(hit-po)>400.0){
                colour += pre+te*0.5+vec3(1.5)*0.25;
            }
        }
    }
    colour /= 1.5;
    vec4 data = texture(iChannel0,th);
    data += vec4(colour,1);
    fragColor = data;
}
