void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 mouse = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;

    float cubeSize = max(0.1, mouse.x);

    float borderSize = abs(mouse.y);

    float m = min(cubeSize, borderSize * cubeSize);

    vec2 d = clamp(uv, vec2(-cubeSize + m), vec2(cubeSize - m));

    float rc = length(vec2(d - uv));

    vec3 color = vec3(0.0);

    color = vec3(1.0, 0.0, 0.6) * smoothstep(4.0 / iResolution.y, 0.0, rc - m);

    fragColor = vec4(color ,1.0);
}
