void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized and shifted pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ab = (fragCoord * 2. - iResolution.xy)/ iResolution.y;

    vec3 col = vec3(0.,0.,0.);

    col.r += 0.1/length(-ab-cos(uv.x*iTime)) + 0.5*cos(uv.y + 4.*iTime);
    col.g += 0.1/length(ab-sin(uv.x*iTime)) + 0.5*cos(uv.x + 2.7*iTime);
    col.b += 0.1/length(ab-cos(uv.y*iTime)) + 0.5*cos(uv.x + iTime);

    col = col * step(-0.4, uv.y-1.5*uv.x);
    col = col * (1.-step(1.-1.2, uv.y-1.7*uv.x));
    // Output to screen
    fragColor = vec4(col,1.0);
}
