float _noise(vec2 uv) {
    // x, y = 0. .. 1.
    int a = int((15673.+uv.x)*uv.x*14268.)%2753;
    int b = int((76512.+uv.y)*uv.y*10021.)%1913;
    return fract(sin(float(a^b)));

}


vec3 screen(vec3 col1, vec3 col2) {
    return 1. - (1.0 - col1) * (1.0 - col2);
}

float _angle(vec3 c, vec2 uv){
    vec2 p = uv-c.xy;
    return (
        sign(p.y)*acos(p.x / length(p))
    );
}


float _map(vec2 uv, vec2 uv1, float pos, float width) {
    return exp(
        -width
        // * (1. + .7*abs(sin(iTime*3.))) // disable brightness animation
        * pow(
            .01*(
                4.*sin(111.*_angle(vec3(0.), uv) + iTime*11.)
            )
            + dot(uv, uv) * (_angle(vec3(0.), uv1) + 3.614) * .51
            - pos,
    2.));
}


vec3 aaa(vec2 uv, vec3 color, float pos, float width) {
    return color * max(
          _map(uv, uv, pos, width)
        , _map(uv, vec2(uv.x, -uv.y), pos, width)
    );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.y*2.2 - vec2(iResolution.x/iResolution.y+.85, 1.0);
    vec3 col = vec3(.2, .09, .0);


    vec3 red = vec3(1., .0, .0);
    vec3 blue = vec3(.0, .3, .9);
    vec3 cyan = vec3(.0, .6, .5);
    vec3 yellow = vec3(1., 1., .0);

    vec3 c1 = aaa(uv, red, .35, 170.);
    vec3 c2 = aaa(uv, yellow, .47, 80.);
    vec3 c3 = aaa(uv, blue, .2518, 170.);
    vec3 c4 = aaa(uv, cyan, .62, 30.);

    col = screen(col, c1); 
    col = screen(col, c2); 
    col = screen(col, c3); 
    col = screen(col, c4); 

    vec3 c5 = aaa(uv, .2*cyan, 1.62, 10.);
    col = screen(col, c5); 


    float n = .08 * _noise(uv);
    fragColor = vec4(n + col*.9,1.0);

}
