#define j(n,m) ((floor(n*m)+.5)/m)
void mainImage( out vec4 c, vec2 f )
{
    vec2 e = iResolution.xy,
         n = (f-e*.5)/e.y*3.3,
         x = n*20., 
         o = vec2(3.,18.),
         p;
    float m = 1.,
          t = iTime,
          a = o.x*o.y,
          i;
    for(i = 0.; i<a;i+=1.){
        p = vec2(mod(i,o.x),floor(i/o.x));
        p+=sin(t+i);
        m = min(m,length(abs(n)-(p/o)));
    }
    c = vec4(1.-pow(m,1./3.));
}
