void mainImage(out vec4 O, vec2 F) {
    vec2 r = iResolution.xy, u = (F+F-r)/r.y;    
    O.rgb*=0.;

    for (float i; i<18.; O.rgb +=
    .003/(abs(length(u+u*u)-i*.04)+.0009)                  
    * (cos(i*vec3(0,1,3))+01.)                           
    * smoothstep(.5,.7, abs(tan((length(mod(iTime,2.)-i*.1)-1.))))
    ) u*=mat2(sin((iTime+i++)*.05 + vec4(0,33,11,0)));
}
