vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,.5)
        //-vec2(1.,.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.x += iTime/t1/12.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 9; k++){
        vec2 uv1 = uv;
        uv = (uv+t2)/scale;
        t2 =
            triangle_wave(uv+.5)
            //triangle_wave(uv+.5)*sign(.5-uv.x)
        ;
        uv = fract(t2+triangle_wave(uv.yx)+.5);
        col.x =
            max(max((t2.y+t2.x*sign(uv.x)),abs(uv.y+uv.x))/6.,col.x)
        ;
        col =
            max(abs(col-1.+col.x),col/3.);
            //max((col-1.+col.x/1.5),(1.-col-col.yzx));
        //if(uv.y < .5) uv.y += .5;
        //if(uv.x < .5) uv.x += .5;
    }
    fragColor = vec4(col,1.0);
}

//remixed from https://www.shadertoy.com/view/cdXXRB

#define sqmod(x,y) floor(mod(floor(x)/4.,y*y)/y)
//#define sqmod(x,y) max(floor(mod(floor(x),y*y)/y),mod(floor(x)*floor(x),y))
//#define sqmod(x,y) max(mod(floor((x)/2.),y),mod(floor((x)*2.),y/2.))


vec2 mainSound(int samp, float time){
 float tempo = 1.;
 time /= tempo;
 //time *= (1.+sqmod(time*2.,2.));
 //time /= (1.+sqmod(time*2.,2.));

 //time += floor(time*8.+floor(time/8.))/8.;


 float s1 =
      4.;

  float
  m4 =
      1.-sqmod(time,2.)
  ,
  t=
      time/(1.+m4)
  ;
  //t += floor(t*8.)*8.;
  float m3 =
      sqmod(floor(t*s1*2.)+t*(.5-m4)*2.,s1);

  t *=
      s1*s1
  ;
  float m2 =
      1. + sqmod(t/s1,s1)
  ;

  float a = 128.*sqrt((1.-sqrt(max(fract(t/s1/s1),fract(t/s1)))))*.2/(1.+m4),

  nb = time*tempo*pow(2.,(m3+m2)/5.+6.5)*4./(1.+mod(floor(t),s1))/2.;

  return
      abs(.5-vec2(fract(nb*.998*2.),fract(nb)))*a
      //vec2(fract(time*nb*.998*(1.+m4)),fract(time*nb*(1.+m3)))*a
  ;

}
