void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 2.0;
    vec3 ro = vec3(0, 0, 3);
    vec3 rd = normalize(vec3(uv.xy, -2.0));
    vec3 col = vec3(0);
    float t = 0.0;
    for(int i = 0; i < 64; i++)
    {
        vec3 pos = ro + t*rd;
        float d = 0.0;
        // Check for intersection with each wall
        if(abs(pos.x) > 1.0) d = abs(pos.x) - 1.0;
        if(abs(pos.y) > 1.0) d = abs(pos.y) - 1.0;
        if(abs(pos.z) > 1.0) d = abs(pos.z) - 1.0;
        // Compute color based on which wall was hit
        if(d > 0.0)
        {
            if(abs(pos.x) > abs(pos.y) && abs(pos.x) > abs(pos.z))
            {
                // Red wall
                col = vec3(1, 0, 0);
            }
            else if(abs(pos.y) > abs(pos.x) && abs(pos.y) > abs(pos.z))
            {
                // Green wall
                col = vec3(0, 1, 0);
            }
            else
            {
                // Blue wall
                col = vec3(0, 0, 1);
            }
        }
        col += exp(-d*d*4.0)*0.1;
        t += 0.1;
    }
    fragColor = vec4(col, 1.0);
}
