void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy / iResolution.xy);

    //Create an offset based on time
    vec2 offset = vec2(iTime*0.1, iTime*0.05);

    //Create a sine wave pattern
    float sineWave = sin((uv.x + offset.x)*100.0)*0.5+0.5;

    //Mix colors 
    vec3 color = mix(vec3(1.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0), sineWave);

    //Output the color
    fragColor = vec4(color, 1.0);
}
