float reScale(float a1, float a2, float b1, float b2, float n){
    return b1 + ((n-a1)*(b2-b1))/(a2-a1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

    st-=0.5;

    float x = st.x *reScale(-1., 1., 0.3, 2.,  sin(iTime * 5. + st.y*iTime))*iTime * 0.1;
    float y = st.y*reScale(-1., 1., 0.3, 2.,  sin(iTime * 5. + st.x*iTime))*2.;

    float desface = iTime * -0.005;

    float c = smoothstep(length(abs(vec2(x, y))+desface), 0.164, 0.140);

    vec3 colorTime = vec3(iTime*0.01, iTime*0.05, iTime*0.09);
    vec3 color = vec3(1., 0., 0.5);

    color = mix(color, colorTime, vec3(c));

    fragColor = vec4(color,1.0);
}
