void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 brickColor = vec4(.8, .25, .2, 1.0);
    vec4 cementColor = vec4(.82, .82, .82, 1.0);

    vec2 squareSize = vec2(50.0, 25.0); // set the size of each square
    vec2 gridPosition = floor(fragCoord / squareSize); // find the grid position of the current pixel
    float xOffset = mod(gridPosition.y, 2.0) * squareSize.x * 0.5; // calculate horizontal offset for even rows
    vec2 squarePosition = mod(fragCoord - vec2(xOffset, 0.0), squareSize); // find the position of the current pixel within its square

    fragColor = brickColor;
    if (squarePosition.x > squareSize.x - 2.0 || squarePosition.y > squareSize.y - 2.0) {
        fragColor = cementColor;
    }
}
