vec4 heatGamma(float temperature){
    float r = smoothstep(500.,1300.,temperature);
    float g = smoothstep(1300.,5000.,temperature);
    float b = smoothstep(4000.,10000.,temperature);
    return vec4(r,g,b,1.);
}
#define K 1.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    fragColor = heatGamma(texture(iChannel0,uv).a);
}

//новое тепло в точке
float calculateHeat(float s,float l,float r,float t,float b, float k){
    return s + k/200.*(-4.*s + l + r + t + b);
}
vec2 rp(vec2 p){
    return p/iResolution.xy;
}
//пересчёт тепла
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    float oldHeat = texture(iChannel0,rp(xy)).a;
    float l = 0.;
    float r = 0.;
    float t = 0.;
    float b = 0.;
    if(xy.x > 1.){
        l = texture(iChannel0,rp(xy+vec2(-1.,0.))).a;
    }
    if(xy.x < iResolution.xy.x-1.){
        r = texture(iChannel0,rp(xy+vec2(1.,0.))).a;
    }
    if(xy.y > 1.){
        b = texture(iChannel0,rp(xy+vec2(0.,-1.))).a;
    }
    if(xy.y < iResolution.xy.y-1.){
        t = texture(iChannel0,rp(xy+vec2(0.,1.))).a;
    }
    float newHeat = calculateHeat(oldHeat,l,r,t,b,50.);
    if(iMouse.z>0.&& length(xy - iMouse.xy)<30.){
        newHeat += smoothstep(30.,0.,length(xy - iMouse.xy))*100.;
    }
    if(newHeat < .0){
        newHeat = 1.;

    }
    fragColor = vec4(0.0,0.0,0.0,newHeat);
}
