mat2 rot2D(float t)
{
    float s = sin(t);
    float c = cos(t);
    return mat2(c, -s, s, c);
}

void _mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.f*(fragCoord.xy/iResolution.xy)-1.f;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 3.f;
    uv.x = -uv.x;

    uv *= rot2D(length(uv)*0.15-1.);


    vec2 idx = floor(uv);
    uv -= idx;

    vec3 col = vec3((int(idx.x)&1) ^ (int(idx.y)&1));

    fragColor = vec4(col,1.0);
}


//From FabriceNeyret2's comment
void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 3.* ( u+u - R ) / R.y;
    U.x = -U.x;   
    U = sin(3.14 * U * mat2( cos( length(U)*.15-1. + vec4(0,33,11,0) ) ) );
    float v = U.x*U.y;
    O = vec4( .5 - v / fwidth(v) );
}
