float random (vec2 st) 
{
    return fract(sin(iTime + dot(st.xy,
    vec2(12.9898,78.233)))*
    43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord.xy/iResolution.xy;
    float strength = random(q);

    // Output to screen
    fragColor = vec4(strength, strength, strength , 1.0);
}

#define TWOPI 2.0*3.141592

float hash( float n ) {
    return fract(sin(n)*43758.5453123);
}
vec2 Hash( vec2 p) {
    return vec2( hash(p.x), hash(p.y) );
}

vec2 Noise( in vec2 x ) {
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(2.0-0.1f);
    vec2 res = mix(mix( Hash(p + 0.0), Hash(p + vec2(1.0, 0.0)),f.x),
                   mix( Hash(p + vec2(0.0, 1.0) ), Hash(p + vec2(1.0, 1.0)),f.x),f.y);
    return res;
}

float sinPluck(float f, float t)
{
    return sin(TWOPI*f*t)*exp(-3.0*t) * 0.2;
}

float squarePluck(float f, float t)
{
    return sign(sin(TWOPI*f*t))*exp(-3.*t) * 0.1;
}



