// From SnoopethDuckDuck: 241 chars! (was 233 before FabriceNeyret2 reported a problem with previous entry)
//*
#define R iResolution
void mainImage(out vec4 O, vec2 U)
{
    U = (U+U - R.xy) / R.y;
    O = vec4(fract(U.y * 4.) < .5);

    float t = abs(mod(iTime, 4.) - 2.);

    #define B(S) abs(abs(U.x S.4)-.8) < .4 - t * .1

    O = abs(U.y - t * .75 + .75) < .25 ?
            B(+) ? vec4(1, .9, .3, 1) :
            B(-) ? vec4(0, .1, .5, 1) :
                O : O;
}
/**/

