// degenerative values from fractional value from high frequency sinus values
float rand(float x)
{
    return fract(sin(x) * 4973.363);
}

// random for 2dimension value
vec2 rand2(vec2 v)
{
    v = vec2(
        dot(v, vec2(127.35, 224.52)),
        dot(v, vec2(269.27, 111.42)));
    return fract(sin(v) * 4973.363);
}

// Remap to [-1;+1]
vec2 remap(vec2 i)
{
    return i * 2. - 1.;
}

// Unmap to [+0;+1]
float unmap(float i)
{
    return i * .5 + .5;
}

// animate uv
vec2 PanUV(vec2 uv)
{
    return uv + iTime * 3.5;
}

// noise gradient
float noise(vec2 uv)
{
    vec2 i = floor(uv);
    vec2 f = fract(uv);
    vec2 xo = vec2(0., 1.);

    // smooth interpolator
    vec2 u = smoothstep(0., 1., f);

    // get 4 corners random directions values
    //  note the importance of remapping to avoid loss of distribution
    float v00 = dot(remap(rand2(i+xo.xx)), f-xo.xx);
    float v01 = dot(remap(rand2(i+xo.xy)), f-xo.xy);
    float v10 = dot(remap(rand2(i+xo.yx)), f-xo.yx);
    float v11 = dot(remap(rand2(i+xo.yy)), f-xo.yy);

    // bilinear interpolation
    //  note the unmap to original range to preserve distribution
    return unmap(mix( mix(v00, v10, u.x),
                      mix(v01, v11, u.x), u.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 10.;

    // noise 2d
    float r = noise(PanUV(uv));

    vec3 col = vec3(r,r,r);

    // Output to screen
    fragColor = vec4(col,1.0);
}
