/**************************************************************************

MOVE YOUR MOUSE to check the  gradient of the image in different directions

**************************************************************************/
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    vec4 color = texture(iChannel0, uv);

    // get gradient
    float dx = dFdx(color.r);
    float dy = dFdy(color.r);

    float angle = iMouse.x/iResolution.x*360.+90.; 
    float radian = radians(angle);
    float gradient = dx * cos(radian) + dy * sin(radian);
    gradient*=2.;
    gradient=pow(gradient,0.5);
    fragColor = vec4(gradient, gradient, gradient, 1.0);
}
