void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    //uv.x *= iResolution.x / iResolution.y;

    float size = 8.0;

    //vec2 Pos = size*uv;
    vec2 Pos = floor(size*uv);     // floor effectively does steps
    //vec2 Pos = floor((.75+0.25*sin(0.25*iTime))*size*uv);

    float PatternMask = mod(Pos.x + mod(Pos.y, 2.0), 2.0);
    vec4 res = PatternMask * vec4(1.0, 1.0, 1.0, 1.0);

    res.w = 1.0;
    //res.w = uv.x*uv.y;
    fragColor = res;
}
