#define COLS 320.0
#define ROWS 180.0
#define DELAY 3.0

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 scaledRes;
    modf(uv.x * COLS, scaledRes.x);
    modf(uv.y * ROWS, scaledRes.y);
    uv = scaledRes / vec2(COLS, ROWS);

    float modulator = texture(iChannel1, vec2(uv.x, 0.0)).r / 2.0 + 0.5;
    float wipe = min((iTime-DELAY)*modulator,1.0) * step(DELAY, iTime);

    if ((uv.y + wipe) < 1.0) {
        fragColor = texture(iChannel0, uv+vec2(0.0, wipe));
    } else {
        fragColor = vec4(0.0);
    }
}
