void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize coordinates
    vec2 uv = fragCoord / iResolution.xy;

    // Generate a circle
    float r = length(uv - 0.5);
    float a = atan(uv.y - 0.5, uv.x - 0.5);

    // Animate the circle
    float t = iTime * 0.5;
    r += sin(t) * 0.1;
    a += t * 5.0;

    // Calculate color based on radius and angle
    float c = sin(r * 10.0 + a * 5.0);
    vec3 color = vec3(c, abs(c), c * 0.5);

    // Add some visual effects
    color += sin(uv.x * 50.0) * 0.1;  // Horizontal waves
    color += sin(uv.y * 50.0) * 0.1;  // Vertical waves
    //color += sin(t * 5.0) * 0.9;       // Flashing effect
    color *= sin(t * 10.0) * 0.5 + 0.5; // Fade in/out effect

    // Distort the image with a swirling effect
    float dis = sin(t) * 0.1;
    uv -= dis;
    uv *= mat2(cos(t), -sin(t), sin(t), cos(t));
    uv += dis;

    // Add some psychedelic colors
    color += sin(uv.x * 100.0) * 0.1;  // Horizontal stripes
    color += sin(uv.y * 100.0) * 0.1;  // Vertical stripes
    color += sin(r * 100.0) * 0.1;     // Radial stripes
   // color *= sin(t * 20.0) * 0.5 + 0.5; // Color cycling effect

    // Output color
    fragColor = vec4(color, 1.0);
}
