float radialPositionColorShiftStart = 10.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = iTime * 0.2;
    float colorShift = iTime * 2.0;
    float colorDensity = 20.0;
    float radialColorShift = 5.0;
    float radialPositionColorShift = radialPositionColorShiftStart + (8.0 * sin(iTime * 0.8));

    vec2 normalizedCoord = fragCoord / iResolution.xy * 2.0 - 1.0;
    vec2 bigNormalizedCoord = fragCoord / iResolution.xy * 2.0 - 1.0;

    for (float i = 0.0; i < 32.0; i += 1.0)
    {
        normalizedCoord = abs(normalizedCoord);
        normalizedCoord -= 0.5;
        normalizedCoord *= 1.1;
        normalizedCoord *= mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
        );
    }

    float radialPosition = 0.0;
    if (length(bigNormalizedCoord) != 0.0)
    {
        if (bigNormalizedCoord.y > 0.0)
        {
            radialPosition = acos(bigNormalizedCoord.x/length(bigNormalizedCoord));
        }
        else
        {
            radialPosition = (3.1415926536 * 2.0) - acos(bigNormalizedCoord.x/length(bigNormalizedCoord));
        }
    }
    fragColor = vec4(0.5 * sin((colorDensity * length(normalizedCoord)) + (colorShift / 2.0) + (0.3 * radialColorShift * length(bigNormalizedCoord) + (0.79 * radialPositionColorShift * radialPosition))) + 0.5, 
                    0.5 * sin((colorDensity * length(normalizedCoord)) + (colorShift / 3.3) + (0.83 * radialColorShift * length(bigNormalizedCoord) + (0.54 * radialPositionColorShift * radialPosition))) + 0.5, 
                    0.5 * sin((colorDensity * length(normalizedCoord)) + (colorShift / 4.85) + (0.57 * radialColorShift * length(bigNormalizedCoord) + (0.34 * radialPositionColorShift * radialPosition))) + 0.5, 1.0);
}
