{
    vec2 R=iResolution.xy;
    c += .04/abs(length(u+=u-R)/R.y-.4/cos((fract(atan(u.x,
    u.y)/(u.x=6.2832/(round(iMouse.x/R.x*9.)+3.)))-.5)*u.x)) - c;}


