#define BOUNDING_BOX_FROM_DDA

#ifdef BOUNDING_BOX_FROM_DDA
#else
vec2 boxIntersection( vec2 ro, vec2 rd, vec2 boxSize ) // https://iquilezles.org/articles/intersectors
{
    vec2 m = 1.0/rd;
    vec2 n = m*ro;
    vec2 k = abs(m)*boxSize;
    vec2 t1 = -n - k;
    vec2 t2 = -n + k;
    float tN = max( t1.x, t1.y );
    float tF = min( t2.x, t2.y );
    if( tN>tF || tF<0.0) return vec2(-1.0);
    return vec2( tN, tF );
}
#endif

#define MAX_DIST 1000.0

float plane_intersection(vec3 pp, vec3 pn, vec3 ro, vec3 rd)
{
    return dot(pp - ro,pn)/dot(rd,pn);
}

#define pi 3.1415926

#define EXP 3
#define COUNT (1 << EXP)
#define MASK (COUNT - 1)
