vec4 plane(ivec2 mapPos)
{
    float s = mix(0.25, 2.0, 0.5 + 0.5 * cos(iTime/11.0));
    vec2 t = vec2(iTime) / vec2(1,2);
    // can be replaced by precalculated constants
    vec2 h[COUNT] =
        vec2[]
        (
            s*sin(t + 2.0 * pi * 0.0 / 8.0),
            s*sin(t + 2.0 * pi * 1.0 / 8.0),
            s*sin(t + 2.0 * pi * 2.0 / 8.0),
            s*sin(t + 2.0 * pi * 3.0 / 8.0),
            s*sin(t + 2.0 * pi * 4.0 / 8.0),
            s*sin(t + 2.0 * pi * 5.0 / 8.0),
            s*sin(t + 2.0 * pi * 6.0 / 8.0),
            s*sin(t + 2.0 * pi * 7.0 / 8.0)
        );

    vec2 hm = vec2(h[(mapPos.x + 0) & MASK].x, h[(mapPos.y + 0) & MASK].y);
    vec2 hp = vec2(h[(mapPos.x + 1) & MASK].x, h[(mapPos.y + 1) & MASK].y);
    float offset = dot(vec2(0.5), (hp + hm)/2.0);
    vec3 n = normalize(vec3(vec2((hp - hm).x/2.0, (hp - hm).y/2.0), 1.0));
    return vec4(n, 1.0-offset);
}

float checker(vec2 p)
{
    if(cos(iTime/6.0) < 0.0) p += 0.5;
    return step(0.0, sin(pi * p.x + pi/2.0)*sin(pi *p.y + pi/2.0));
}

// (could also be named process_voxel or process_cell)
vec4 process_subobject(ivec2 mapPos, vec3 ro, vec3 rd, float tmin, float tmax)
{
    vec4 no = plane(mapPos);
    float d = plane_intersection(vec3(mapPos,no.w), no.xyz, ro, rd);
    vec3 n = no.xyz;

    return vec4(n, d);
}

#define MAX_RAY_STEPS 64

#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

void mainImage( out vec4 fragColor, in vec2 I )
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    //I = 2.0 * I.xy / R - 1.0;
    //I.x *= R.x / R.y;
    I = (2.0 * I - R) / R.y; // concise pixel-position mapping thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 1.25 * pi / 2.0;

    vec3 ro = vec3(0.0, 0.0, -5.0 );
    vec3 rd = normalize(vec3(I.xy, 2.0)); // NOTE: ray-direction must be unit-vector

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ro = M * ro;
    rd = M * rd;

    ro.z += 2.0;
    vec3 color = vec3(0);
    vec4 result = vec4(vec3(0.0), MAX_DIST);

    {
        // Initialization of https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting
        vec2 deltaDist = 1.0 / abs(rd.xy);
        ivec2 rayStep = ivec2(sign(rd));
        ivec2 mapPos = ivec2(floor(ro));
        vec2 sideDist = (sign(rd.xy) * (vec2(mapPos) - ro.xy) + (sign(rd.xy) * 0.5) + 0.5) * deltaDist;

#ifdef BOUNDING_BOX_FROM_DDA
        float tmin = 0.0;
#endif
        for (int i = 0; i < MAX_RAY_STEPS; i++)
        //for (int i = min(iFrame,0); i < MAX_RAY_STEPS; i++) // prevent unrolling loop to prevent crash
        {
            {
#ifdef BOUNDING_BOX_FROM_DDA
                // try to reconstruct far distance to bounding box from DDA:
                float tmax = length(vec2(lessThanEqual(sideDist.xy, sideDist.yx)) * sideDist) / length(rd); // hopefully this is correct...
#else
                // It appears to be safer to just do the box-intersection here (potential for optimization).
                vec2 bounds = boxIntersection(ro.xy - vec2(mapPos) - 0.5, rd.xy, vec2(0.5)); // bounding box
                float tmin = bounds.x; float tmax = bounds.y;
#endif
                result = process_subobject(mapPos, ro - 0.5, rd, tmin, tmax);

                if(result.w >= max(0.0, tmin) && result.w <= tmax)
                {
                    color = vec3(1.0);
                    //color /= result.w;
                    //color *= 0.5 + 0.5 * normalize(result.xyz);
                    vec3 dst = ro + rd * result.w;
                    vec3 n = result.xyz;
                    color = mix(color, 0.5 + 0.5 * n, 0.5 + 0.5 * sin(iTime/5.0));
                    color = mix(color, vec3(0.5 + 0.5 * checker(dst.xy)), 0.5 + 0.5 * cos(iTime/3.0));

                    break;
                }
#ifdef BOUNDING_BOX_FROM_DDA
                tmin = tmax;
#endif
            }
            // Core from https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting
            // https://en.wikipedia.org/wiki/Digital_differential_analyzer_(graphics_algorithm)
            // "The [voxel] raycasting code is somewhat based around a 2D raycasting tutorial found here:
            //  http://lodev.org/cgtutor/raycasting.html" (fb39ca4)
            bvec2 mask = lessThanEqual(sideDist.xy, sideDist.yx);
            sideDist += vec2(mask) * deltaDist;
            mapPos += ivec2(vec2(mask)) * rayStep;
        }
    }

    vec3 ambient = vec3(0.1);
    vec3 lightdir = normalize(vec3(3.0, 2.0, 1.0));
    color *= mix(ambient, vec3(1.0), clamp(dot(lightdir, result.xyz), 0.0, 1.0));

    fragColor = vec4(sqrt(color), 1.0);
}
