#define R iResolution.xy

mat2 rot(float a) {
    float s = sin(a); 
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float pattern(vec2 uv, float tile) {
    float x = cos(uv.y*tile);
    float y = cos(uv.x*tile);
    uv *= reflect(uv, vec2(x, y));
    vec2 lv = smoothstep(0.0, 1.0, fract(uv*tile));
    return pow(max(0.0, 1.0 - min(lv.x*tile, lv.y*tile)), tile*0.25);
}

void mainImage( out vec4 O, in vec2 fc )
{
    vec3 col = vec3(0.0);
    vec2 uv = (fc-0.5*R.xy) / R.y;
    float a = radians(iTime*30.0);
    if (iMouse.z > 0.01) uv += (iMouse.xy-0.5*R.xy) / R.xy;
    uv.xy *= rot(a);
    col = vec3(pattern(uv, 16.0));
    O = vec4(col, 1.0);
}
