float  f_n_dotproduct(vec2 o_p1, vec2 o_p2){
    return (
        o_p1.x * o_p2.x 
        + o_p1.y * o_p2.y
    );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_fc = (fragCoord.xy-iResolution.xy*0.5)/iResolution.y;
    vec2 o_mo = (iMouse.xy-iResolution.xy*0.5)/iResolution.y;

    float n_dist = length(o_fc-o_mo);
    if(o_fc.x > o_mo.x){
        n_dist = f_n_dotproduct(o_fc-o_mo,o_fc-o_mo);
    }

    fragColor = vec4(
        smoothstep(0.1, 0.2,n_dist)
    );
}
