void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float progress = iMouse.x / iResolution.x;

    vec2 uvDivided = fract(uv * vec2(10.0,0.));
    uvDivided += uv;

    vec2 displacement1 = mix(uv, uvDivided, pow(progress,2.));

    vec4 tex = texture(iChannel0, displacement1);

    fragColor = vec4(tex);
}
