vec2 cell(vec2 uv, float zoom) {
    uv *= zoom;
    float id_y = mod(uv.y, 2.0);
    float id_x = mod(uv.x, 2.0);
    float dir_x = step(1.0, id_y) < 1.0 ? 0.0 : 1.0;
    float dir_y = step(1.0, id_x) < 1.0 ? 0.0 : 1.0;
    uv.x += dir_x * 0.5 + dir_x == 0.0 ? sin(iTime) : 1.0 - sin(iTime);
    uv.y += dir_y * 0.5 + dir_y == 0.0 ? sin(iTime) : 1.0 - sin(iTime);
    return fract(uv);
}

float circle(vec2 uv, float radius) {
    uv -= 0.5;
    float d = length(uv);
    float t = smoothstep(radius, radius - 0.1 * radius, d);
    return t;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / min(iResolution.x, iResolution.y);
    vec2 cell = cell(uv, 10.0);
    float c = circle(cell, 0.1);
    fragColor = vec4(vec3(c), 1.0);
}
