#define pi 3.14159265359

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // Time variable for animation
    float time = iTime;

    // Calculate ball positions
    float radius1 = 0.3; // Raio da primeira bola
    float rotationSpeed1 = -0.5; // Velocidade de rotação da primeira bola

    // Radius of the balls
    float ballRadius = 0.02;

    // Check if pixel is inside either of the balls
    vec4 col = vec4(0.0);


     for (float i = 0.; i < 3.; i += 0.50){
         vec2 ballPosition1 = vec2(radius1 * cos(rotationSpeed1 * time + (500.*pi)+i) * 2.0, radius1 * sin(rotationSpeed1 * time + (500.*pi)+i));
        // Calculate distances from pixel to ball centers
        float distanceToBall1 = length(uv - ballPosition1);

        col += 0.02* vec4(vec3(1.0, 0.5, 0.0), 1.0)/length(distanceToBall1);

         vec2 ballPosition2 = vec2(radius1 * cos(rotationSpeed1 * time + (500.*pi) + pi + i) * 2.0, radius1 * - sin(rotationSpeed1 * time + (500.*pi) + i));
        // Calculate distances from pixel to ball centers
        float distanceToBall2 = length(uv - ballPosition2);

        col += 0.02* vec4(vec3(1.0, 0.5, 0.0), 1.0)/length(distanceToBall2);

    }




    fragColor = col;
}
