// Distortion shader with various waves

vec3 ImageMain( in vec2 fragCoord )
{
    // Epic parameters
    float Frequency = 20.0;
    float Amplitude = 0.01;
    float EpicRGBGlich = 1.5;
    float Colorizer = 0.5;
    float uvGlitch = 0.1;
    float Speed = 1.5;

    // Time-based animation
    float time = iTime * Speed;

    // Noise functions
    float noise1 = sin(Frequency * fragCoord.x + time) * Amplitude;
    float noise2 = cos(Frequency * fragCoord.y + time) * Amplitude;
    float noise3 = tan(Frequency * length(fragCoord) + time) * Amplitude;

    // UV glitch thing
    vec2 uv = fragCoord / iResolution.xy;
    uv += vec2(noise1, noise2) * uvGlitch;

    // Color distortion
    vec3 color = texture(iChannel0, uv).xyz;
    color += vec3(noise1, noise2, noise3) * EpicRGBGlich;
    color.r += sin(time) * Colorizer;
    color.g += cos(time) * Colorizer;
    color.b += tan(time) * Colorizer;

    // Combine RGB channels in a trippy way 
    vec3 trippyColor = vec3(color.r, color.g, color.b);
    trippyColor = fract(trippyColor * 3.0);
    color = trippyColor;

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 finalColor = ImageMain(fragCoord);
    fragColor = vec4(finalColor, 1.0);
}
