float pi = 3.1416;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.x;
    vec2 uv = (fragCoord/iResolution.xy);
    //uv = fract(uv*2.);
    // Time varying pixel color
    uv.y = (uv.y -.5)*2.;
    vec3 col;
    float freq = (0. + sin(iTime))*1.;
    float amp = .8;
    float d = abs(uv.y - amp*sin(uv.x*2.*pi-iTime)-0.1*tan(uv.x*100.));
    //d = step(.1,d);
    //d = .1/d;
    float d2 = abs(uv.y - amp*sin(uv.x*2.*pi-2.*iTime*1.)-0.1*tan(uv.x*100.));
    //d = step(.1,d);
    float o = min(d,d2);
    //d = .1/d;
    //d2 = .1/d2;
    //o = step(.1,o);
    o = (.02/(.1+abs(d-d2)))/o;

    col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col*o,1.0);
}
