void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    //------options------

    int size = 60; //ball size
    int speed = 2; //ball speed
    //ball colors
    vec4[3] ballColors = vec4[](
    vec4(1.0, 1.0, 0.0, 1.0),
    vec4(0.0, 1.0, 1.0, 1.0),
    vec4(1.0, 0.0, 1.0, 1.0)
    );
    int colors = 3; //how many colors in "ballColors"

    //back ground colors
    vec4 upperRight = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 upperLeft = vec4(0.0, 0.0, 0.0, 1.0);
    vec4 lowerRight = vec4(0.0, 0.0, 0.0, 1.0);
    vec4 lowerLeft = vec4(1.0, 1.0, 1.0, 1.0);

    //---------------------

    int time = int(iTime*100.0)*speed;
    int Xres = int(iResolution.x)-size*2;
    int Yres = int(iResolution.y)-size*2;

    int Xpos = abs(Xres - time % Xres*2)+int(size);
    int Ypos = abs(Yres - time % Yres*2)+int(size);

    vec2 point = vec2(Xpos, Ypos)/iResolution.xy;

    int boundCnt = time/(Xres/2) + time/(Yres/2);

    float timeWave = abs(sin(iTime));

    //back ground painter
    if(uv.x >= point.x && uv.y>= point.y){
        fragColor = upperRight;
    }
    else if(uv.x <= point.x && uv.y>= point.y){
        fragColor = upperLeft;
    }
    else if(uv.x >= point.x && uv.y<= point.y){
        fragColor = lowerRight;
    }
    else if(uv.x <= point.x && uv.y<= point.y){
        fragColor = lowerLeft;
    }

    //ball painter
    if(length(point*iResolution.xy - uv*iResolution.xy)<=float(size)){
        fragColor = ballColors[boundCnt%colors];
    }
}
