mat2 rX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(
        vec2(c, -s),
        vec2(s, c)
    );
}

// Rotation matrix around the Y axis.
mat2 rY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(
        vec2(c, s),
        vec2(-s, c)
    );
}

// Rotation matrix around the Z axis.
mat2 rZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(
        vec2(c, -s),
        vec2(s, c)
    );
}

// Identity matrix.
mat2 iy() {
    return mat2(
        vec2(1, 0),
        vec2(0, 1)
    );
}
float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r, vec2 offset, in mat2 transform)
{
    p = (p-offset)*transform;
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0.0);

    vec2 b = vec2(0.2,0.3);
    float l = smoothstep(0.1,0.001,0.1*sin(iTime))*0.4;
    float t = sdRoundedBox(p, b, vec4(0.05), vec2(0.0,0.0), rX(l));
    t = min(t, sdRoundedBox(p, b, vec4(0.05) , vec2(-l,0.0), rZ(-l)));
    t = min(t, sdRoundedBox(p, b, vec4(0.05) , vec2(l,0.0), rX(l)));
    t = min(t, sdRoundedBox(p, b, vec4(0.05) , vec2(-l*2.0,0.0), rX(-l)));
    t = min(t, sdRoundedBox(p, b, vec4(0.05) , vec2(l*2.0,0.0), rX(l)));

    col = (t>0.0) ? vec3(0.0) : vec3(1.);
    col *= 0.8 + 0.2*cos(150.0*t);
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(t)) );


    // Output to screen
    fragColor = vec4(col,1.0);
}
