float iterations = 50.0;

float color(float x) {
    return x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from -4 to 4)
    vec2 pos = 4.0 * (iMouse.xy - iResolution.xy / 2.0)/min(iResolution.x,iResolution.y);
    pos = vec2(pos.x,-pos.y);

    vec2 c = 4.0 * (fragCoord - iResolution.xy / 2.0)/min(iResolution.x,iResolution.y);
    c = vec2(c.x,-c.y);

    vec2 z = vec2(0.0,0.0);

    float iter = iterations;
    for (float i = 0.0; i < iterations; i++) {
        z = cm(z,z)+c;
        if (mag(z) > 100.0) {
            iter = i;
            break;
        }
    }

    if (iter != iterations) {
        float log_zn = log(z.x*z.x+z.y*z.y)/2.0;
        float nu = log(log_zn / log(2.0)) / log(2.0);

        iter = iter + 1.0 - nu;
    }

    vec3 colorHSL = vec3(360.0 * color(iter/iterations), 1.0, 0.5);

    float chroma = (1.0-abs(2.0*colorHSL.z-1.0)) * colorHSL.y;

    float h1 = colorHSL.x/60.0;

    float x = chroma * (1.0 - abs(mod(h1,2.0)-1.0));

    vec3 col = vec3(0.0,0.0,0.0);

    if (h1 < 1.0) {
        col = vec3(chroma,x,0.0);
    } else if (h1 < 2.0) {
        col = vec3(x,chroma,0.0);
    } else if (h1 < 3.0) {
        col = vec3(0.0,chroma,x);
    } else if (h1 < 4.0) {
        col = vec3(0.0,x,chroma);
    } else if (h1 < 5.0) {
        col = vec3(x,0.0,chroma);
    } else if (h1 < 6.0) {
        col = vec3(chroma,0.0,x);
    }

    vec3 m = vec3(colorHSL.z-chroma/2.0);

    // Output to screen
    fragColor = vec4(col+m,1.0);
}

vec2 cm(vec2 a, vec2 b) {
    return vec2(a.x*b.x-a.y*b.y,a.y*b.x+a.x*b.y);
}

float mag(vec2 vector) {
    return sqrt(vector.x*vector.x+vector.y*vector.y);
}

vec2 cpow2(vec2 z1, float n) {
    float angle = atan(z1.y,z1.x);
    return pow(mag(z1),n)*vec2(cos(n*angle),sin(n*angle));
}
