void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Calculate the position of the current fragment in the UV space, 
    //with (0, 0) at the bottom left and (1, 1) at the top right.
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Center the UV space around (0.5, 0.5).
    vec2 p = uv - 0.5;


    float zoom = 1.0 + sin(iTime) * 0.5;
    p *= zoom;
    p.x *= iResolution.x / iResolution.y;
    float len = length(p);
    float angle = atan(p.y, p.x);
    float phase = sin(iTime) * 0.1 + len * 40.0;
    float sine = sin(angle * 8.0 + phase) * 0.5 + 0.5;
    fragColor = vec4( sine, sine * 0.5, sine * 0.25, 1.0 );
}
