void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 black_col = vec3(0.,0.,0.);
    vec3 white_col = vec3(1.,1.,1.);

    float size = 50.;
    vec2 coord = fract((fragCoord.xy - iResolution.xy/2.) / size);

    vec3 color = black_col;
    float power = 2.;

    float amount = pow(2.*coord.x - 1., power);
    color = white_col * amount;

    // Output to screen
    fragColor = vec4(color,1.0);
}
