void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/(iResolution.xy - 1.0);

    // Output to screen
    uv *=2.0;
    uv -= 1.0;
    uv.y *= iResolution.y/iResolution.x;

    // Ron's bouncing ball

    float xc,yc,r;
    float ycorner=iResolution.y/iResolution.x;

    r=0.1;
    float t = mod(iTime/2.0,4.0*(1.0-r));
    float ty = mod(iTime/2.5,4.0*(ycorner-r));

    xc=(-1.0+r)+min(abs(t),(4.0*(1.0-r))-abs(t));
    yc=0.0;
    yc=-(ycorner-r)+min(abs(ty),(ycorner-r)*4.0-abs(ty));

    fragColor.b=(uv.x+1.0)/2.0;

    if(length( uv-vec2(xc,yc)) < r  ) {
        fragColor.rgb=vec3(1.0,0.5,0.5);
    }

}
