float f1(float p, vec2 uv){
    return 1.-distance(p, uv.y);
}

float f2(vec2 uv, float size, float offs){
    float x = uv.x + offs;
    float p = abs(sin(1.-x*x + iTime)*x/uv.x);

    float wd = f1(p, uv);
    size *= fwidth(uv.y);
    wd = 1. / (1.-wd) * size;

    return wd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ouv = uv;
    uv.x *= 4.0 / uv.x*uv.x;
    uv.x += -2.;
    uv.x = abs(uv.x);
    uv.y /= uv.x*0.5;
    uv.y -= 0.55;
    float uvywow = uv.y;
    uv.y = abs(uv.y);

    vec3 colRatio = mix(vec3(5., 10., 20.), vec3(20., 5., 10.), 1.0-abs(cos(1.0+iTime+uv.x)));
    vec3 col = vec3(f2(uv, colRatio.x, 0.0), f2(uv, colRatio.y, 0.0), f2(uv, colRatio.z, 0.0));
    col += colRatio*0.01;

    col *= clamp(0.0, 1.0, step(0., uvywow)*uvywow + 0.5);

    fragColor = vec4(col, 1.);
}
