vec2 hash2(vec2 p)
{
    p*=mat2(916.,602.,691.,2962.);
    return fract(sin(p)*178322.);
}

float B(vec2 p,vec2 size)
{
    p=abs(p)-size;
    return length(max(p,0.))+min(max(p.x,p.y),0.);
}
float S(vec2 p,vec2 s)
{
    //p.x-=clamp(p.x,0.,0.5);
    return length(p)-s.x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x*=iResolution.x/iResolution.y;
    vec2 p=uv*3.;
    vec2 i=floor(p);
    vec2 s=hash2(i)*fract(abs(sin(iTime*1.2)*2.5));
    vec2 f=p-i-vec2(0.5);
    float a1;float a2;
    a1=step(B(f,s),0.);
    a2=step(S(f,s),0.);
    float b=a1*(step(0.1,s.x)-step(0.2,s.x))*smoothstep(0.6,0.9,sin(iTime*3.+i.y));
    b+=a1*(step(0.2,s.x)-step(0.3,s.x))*smoothstep(0.5,0.8,sin(iTime*2.5+i.x));
    b+=a1*(step(0.3,s.x)-step(0.4,s.x))*smoothstep(0.7,0.8,sin(iTime*4.+i.x+i.y));
    b+=a1*(step(0.05+0.1*sin(iTime),s.x)-step(0.1+0.1*sin(iTime),s.x))*smoothstep(0.7,0.8,sin(iTime*16.+i.x+i.y));

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col=mix(col,col*vec3(fract(hash2(i)*7.+iTime),fract(hash2(i)*hash2(i)*58.+iTime)),b);

    fragColor = vec4(col,1.0);
}
