float N21 (vec2 p){
    return fract( sin(p.x *  100. + p.y * 6574.) * 5647.);
}

float SmoothNoise(vec2 uv){
    vec2 lv = smoothstep(0., 1., fract(uv));
    vec2 id = floor(uv);

    float bl = N21(id);
    float br = N21(id + vec2(1,0));
    float b = mix(bl, br, lv.x);


    float tl = N21(id + vec2(0, 1));
    float tr = N21(id + vec2(1, 1));
    float t = mix(tl, tr, lv.x);

    return mix(b, t, lv.y);
}

float smoothNoise2(vec2 uv){ 
    float c = SmoothNoise(uv*4.);
    c += SmoothNoise(uv*8.)* 0.5;
    c += SmoothNoise(uv*16.) * 0.25;
    c += SmoothNoise(uv*32.) * 0.125;
    c += SmoothNoise(uv*64.) * 0.0625;

    return c/2.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float c = smoothNoise2(uv) + 0.2 + 0.4*sin(iTime);

    vec3 col = vec3(c/1.5, c/1.2, c);

    // Output to screen
    fragColor = vec4(col,1.0);
}
