float ray = 150.0;
float limit = 0.5;
float zoomCoeff = 4.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fromMouse = fragCoord - iMouse.xy;
    float distNormalized = min(1.0, length(fromMouse)/ray);

    vec2 uv = fromMouse / zoomCoeff + iMouse.xy;

    if (distNormalized > limit) {
       float coeff = (distNormalized - limit)/(1.0 - limit);
       uv = uv * (1.0 - coeff) + fragCoord * coeff;
    } 

    // sample texture and output to screen
    fragColor = texture(iChannel0, uv/iResolution.xy);
}
