mat2 rot2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.2,e; vec3 p,q=vec3(-1,0,1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    q.xz*=rot2D(iTime*.3);
    q.yz*=rot2D(iTime*.3);

    for(j=0.;j++<2.;)
        p=abs(abs(reflect(p,q))),
        d*=max(min(p.y,p.z),p.x),
        e-=d;

}o+=vec4(exp(p/2.),1)*3./i;
}
