void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;



    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,2));

    float a = 1.15;
    if(uv.x * uv.x + uv.y * uv.y < a * a){
        col = 0.7 + 0.7*cos(iTime+uv.xyx+vec3(0,2,4));
    }


    float b = 1.0;
    if(uv.x * uv.x + uv.y * uv.y < b * b){
        col = 0.7 + 0.7*cos(iTime+uv.xyx+vec3(2,0,6));
    }


    float c = 0.75;
    if(uv.x * uv.x + uv.y * uv.y < c * c){
        col = 0.4 + 0.4*cos(iTime+uv.xyx+vec3(2,6,0));
    }

    float d = 0.5;
    if(uv.x * uv.x + uv.y * uv.y < d * d){
        col = 0.7 + 0.7*cos(iTime+uv.xyx+vec3(6,2,0));
    }

    float e =0.3*cos(50.0*cos(iFrameRate));
    if(uv.x * uv.x + uv.y * uv.y < e * e){
        col = vec3(0,0,0);
    }

    float f =0.3*sin(50.0*cos(iFrameRate));
    if(uv.x * uv.x + uv.y * uv.y < f * f){
        col = vec3(1,1,1);
    }

    float g =0.15*sin(50.0*cos(iFrameRate));
    if(uv.x * uv.x + uv.y * uv.y < g * g){
         col = 0.7 + 0.7*cos(iTime+uv.xyx+vec3(0,2,2));
    }

    // Output to screen
    fragColor = vec4(col,1.1);
}
