// Public Domain under http://unlicense.org, see link for details.

// Visually detect non-monotonicity of sin().
// By default only [1.0;1.5+eps] is tested.
// White dots mean non-monotonic.

// See also https://www.shadertoy.com/view/dttczs for
// more GLSL math funkiness.

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    float c=exp2(-23.0);
    float lo=1.25+0.25*sin(0.1*iTime);
    lo=floor(lo/c/iResolution.y)*c*iResolution.y;
    float hi=lo+c*iResolution.x*iResolution.y;
    float id=dot(floor(fragCoord),vec2(iResolution.y,1));
    float x=lo+c*id;
    vec3 col=(sin(x)>sin(x+c)?vec3(1):vec3(0.25));
    if(fragCoord.y<16.0)
    {
        float t=2.0*fragCoord.x/iResolution.x;
        if(t<lo) col.z+=0.25;
        if(t>=lo&&t<hi) col.y+=0.25;
        col.x+=exp(-4e2*abs(t-0.5*3.14159265358979));
        if(fragCoord.y<8.0) col.x+=exp(-50.0*fract(10.0*t)*(1.0-fract(10.0*t)));
    }
    fragColor=vec4(col,1.0);
}
