void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Background color
    vec3 backgroundColor = vec3(0.0, 1.0, 0.0); // Cyan

    // Calculate waving effect
    float wavingStrength = 0.1;
    float wavingSpeed = 2.0;
    float wavingOffset = sin(uv.y * 10.0 + iTime * wavingSpeed) * wavingStrength;

    // Calculate gradient positions
    float numLines = 5.0;
    float lineWidth = 0.553;
    float lineSpacing = 1.0 / numLines;
    float linePos = mod(uv.y + wavingOffset, lineSpacing) / lineSpacing;

    // Calculate gradient colors
    vec3 startColor = vec3(0.0, 1.0, 0.0);  // Green
    vec3 endColor = vec3(0.5, 0.0, 0.5);    // Purple
    float gradientPos = smoothstep(lineWidth, 0.0, abs(linePos - 0.5));
    vec3 gradientColor = mix(startColor, endColor, gradientPos);

    // Add noisy grain effect
    float noise = fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
    float grainIntensity = 0.1;
    gradientColor += noise * grainIntensity;

    // Combine gradient color and background color
    vec3 finalColor = mix(backgroundColor, gradientColor, gradientPos);

    fragColor = vec4(finalColor, 1.0);
}
