float circle(vec2 uv, vec2 pos, float _rad, float _blur){
    float rad = _rad;
    float blurLevel = rad - _blur;
    float dist = length(uv + pos);
    return smoothstep(rad, blurLevel, dist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    vec3 col = vec3(0.0,0.0,0.0);
    for(int i = 0; i < 50; i ++){
        col.x += circle(uv, vec2(sin(iTime - float(i)*0.1) * sin(iTime + uv.y * 10.5) * 0.25, cos(iTime * 0.2- float(i)*0.2) * cos(iTime + uv.x * 1.2) * 0.15), 0.04 - float(i)*0.001, 0.05);
        col.y += circle(uv, vec2(sin(iTime - float(i)*0.1) * sin(iTime + uv.y * 20.05) * 0.15, cos(iTime * 0.2- float(i)*0.2) * cos(iTime + uv.x * 5.02) * 0.15), 0.04 - float(i)*0.001, 0.05);
        col.z += circle(uv, vec2(sin(iTime - float(i)*0.1) * sin(iTime + uv.y * 30.1) * 0.15, cos(iTime * 0.2- float(i)*0.2) * cos(iTime + uv.x * 10.12) * 0.15), 0.04 - float(i)*0.001, 0.05);
    }
    // Output to screen
    fragColor = vec4(col.x, col.y, col.z,1.0);
}
