void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 pieCenter = vec2(0.5, 0.5);
    float pieRadius = 0.3;
    int numSections = 10;

    float sectionAngle = 2.0 * 3.14159 / float(numSections);
    float angle = atan(uv.y - pieCenter.y, uv.x - pieCenter.x);
    float distance = length(uv - pieCenter);

    if (distance < pieRadius)
    {
        int sectionIndex = int(floor((angle + 3.14159) / sectionAngle));

        vec3 color;
        if (sectionIndex == 0) color = vec3(1.0, 0.0, 0.0);    
        else if (sectionIndex == 1) color = vec3(0.0, 1.0, 0.0); 
        else if (sectionIndex == 2) color = vec3(0.0, 0.0, 1.0); 
        else if (sectionIndex == 3) color = vec3(0.2, 0.3, 1.0); 
        else if (sectionIndex == 4) color = vec3(0.7, 1.9, 0.0); 
        else if (sectionIndex == 5) color = vec3(0.6, 0.0, 1.0); 
        else if (sectionIndex == 6) color = vec3(0.2, 0.3, 1.0); 
        else if (sectionIndex == 7) color = vec3(0.0, 0.4, 2.0); 
        else if (sectionIndex == 8) color = vec3(0.8, 0.9, 1.0); 
        else if (sectionIndex == 9) color = vec3(1.9, 0.2, 2.0); 
        fragColor = vec4(color, 1.0);
    }
    else
    {

        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
