// Function to convert binary to Gray code
int binaryToGray(int binary) {
    return binary ^ (binary >> 1);
}
int grayToBinary(int gray) { 
    int mask = gray >> 1;
    while (mask != 0) {
        gray ^= mask;
        mask >>= 1;
    }
    return gray;
}



void mainImage( out vec4 fragColor, vec2 fragCoord )
{

    vec2 currentVec = fragCoord/iResolution.xy;
    ivec2 integerPosition = ivec2(currentVec * 65535.);
    int grayRep = binaryToGray(integerPosition.x);
    fragColor = vec4(vec4((binaryToGray(integerPosition.x)))/65535.);   
}
